package functions;

public class NRWP {
	public void add(int a, int b)
	{
		int c=a+b;
		System.out.println(c);
	}
	public void sub(int a, int b)
	{
		int c=a-b;
		System.out.println(c);
	}


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		NRWP a1=new NRWP();
		a1.add(2, 3);
		a1.sub(5, 2);

	}

}
